var z_data = []
for (var i=1; i <= surface_raw_data.rows; i++) {
  var columns = Object.keys(surface_raw_data.data)
  var row = []
  for (var j = 0; j < columns.length; j++) {
    var key = columns[j]
    row.push(surface_raw_data.data[key][i])
  }
  z_data.push(row)
}

var surface_data = {
  type: 'surface',
  z: z_data
}

