  citations,
  content,
  contentSize,
  dateCreated,
  dateModified,
  datePublished,
  description,
  editors,
  embedUrl,
  format,
  funders,
  id,
  isPartOf,
  licenses,
  meta,
  name,
  parts,
  publisher,
  text,
  thumbnail,
  title,
  transcript,
  url,
  version
){
  self <- MediaObject(
    contentUrl = contentUrl,
    alternateNames = alternateNames,
    authors = authors,
    bitrate = bitrate,
    citations = citations,
    content = content,
    contentSize = contentSize,
    dateCreated = dateCreated,
    dateModified = dateModified,
